"""Random numbers for an uneven dice roll."""import randomimport numpy as npclass RandomGen(object):    _random_nums = [1, 2, 3, 4]    _probabilities = [0.1, 0.2, 0.3, 0.4]        def __init__(self):        probs = np.array(self._probabilities)        self._cumprobs = np.insert(np.cumsum(probs), 0, 0)        def next_num(self):        rnd = random.random()        idx = np.digitize(rnd, self._cumprobs, True)        val = self._random_nums[idx - 1]        return val